import React from "react";
import ReactCSSTransitionGroup from "react-addons-css-transition-group";
import logo from "../amri.png";
import "../App.css";
import PropTypes from 'prop-types';

const fixedWrapperStyles = {
    height: '30px',
    position: "relative",
    overflow: "hidden",
    display: "inline-block",
};
const movingWrapperStyles = {
    position: "relative",
    top: "0px",
    display: "inline-block"
};
const texts = [
    "text 1",
    "text 2",
    "text 3"
];
class Home extends React.Component {



    render() {
        return (
            <div className="accueil">
              <ReactCSSTransitionGroup
                transitionName="anim"
                transitionAppear={true}
                transitionAppearTimeout={50000}
                transitionEnter={false}
                transitionLeave={false}
              >
                <h1>HOME</h1>
              </ReactCSSTransitionGroup>
              <img src={logo} alt="amri" />
              <h4>Hello, I am </h4>
              <h2>AMRI</h2>
              <h4>
                I am
                <strong> Developer Web. Passionate about IT .</strong>
                </h4>


                <div style={fixedWrapperStyles}>
        <div style={movingWrapperStyles}>
          {this.props.texts.map((item, index) => (
            <div
              key={'scrolling-text' + index}>
              {item}
            </div>
          ))}
        </div>
      </div>

            </div>



          );
    }
}

export default Home;
